export default function Item({ onDeleteClick }) {
  const todoItems = useContext(TodoItemContext);
  console.log(todoItems);
  return (
    <>
      <div
        className="col-12 d-flex gap-2 justify-content-center mt-5"
       
      >
        {todoItems.map((curElem, ind) => {
          return(
          <>
            <div className="col-4 col-lg-4 text-capitalize"  key={ind}>{curElem.name}</div>
            <div className="col-4 col-lg-3">{curElem.dueDate}</div>

            <button
              className="col-2  col-lg-1 btn btn-outline-danger"
              onClick={() => onDeleteClick(curElem.name)}
            >
              delete
            </button>
          </>
          )
        })}
      </div>
      ;
    </>
  );
}
