export default function Todo() {
  const [todoItems, settodoItems] = useState([]);

  const [buttonText, setButtonText] = useState("CHECK LIST");

  const handleNewItem = (itemName, itemDueDate) => {
    console.log(`New Item Added: ${itemName} Date:${itemDueDate}`);
    const newtodoItems = [
      ...todoItems,
      { name: itemName, dueDate: itemDueDate },
    ];
    settodoItems(newtodoItems);
  };
  const handleDeleteItem = (todoItemName) => {
    const newtodoItems = todoItems.filter((elem) => {
      return elem.name !== todoItemName;
    });
    settodoItems(newtodoItems);
    console.log(`Item Deleted:${todoItemName}`);
  };
  // const defaultTodoItems = []
  // const defaultTodoItems = [{name:'Buy Ghee', dueDate:'Today'}]

  const changeButtonText = (text) => {
    setButtonText(text);
  };

  const removeItems = () => {
    console.log("removeItems function called");
    settodoItems([]);
  };
  return (
    <>
      {/* <TodoItemContext.Provider value={defaultTodoItems}> */}
      <TodoItemContext.Provider
        value={
          // {todoItems:todoItems,
          //   handleNewItem:handleNewItem,
          //   handleDeleteItem:handleDeleteItem}
          { todoItems, handleNewItem }
        }
      >
        {/* //Now state is avilable to the TodoItemContext , pass as a todoItems instead of passing an propss */}
        <Title heading={"Todo App"} react={"(ReactApp)"} />
        <div className="row mt-4">
          <Input />
        </div>
        <Messange></Messange>
        <div className="row">
          <Item onDeleteClick={handleDeleteItem} />
        <div className="showbtn">
          <button
            onDoubleClick={removeItems}
            onMouseOver={() => changeButtonText("Remove All")}
            onMouseOut={() => changeButtonText("CHECK LIST")}
          >
            {buttonText}
          </button>
        </div>
        </div>
      </TodoItemContext.Provider>
    </>
  );
}
